// eslint-disable-next-line no-restricted-globals
const serviceWorkerScope = self;

serviceWorkerScope.onnotificationclick = function handleNotificationClick(
    event
) {
    event.notification.close();

    const { title, body } = event.notification;
    const inAppNotification = { title, body };

    const origin = serviceWorkerScope.location.origin;
    event.waitUntil(
        serviceWorkerScope.clients
            .matchAll({ type: 'window' })
            .then((windowClientList) => {
                const focusClient = windowClientList.find((client) =>
                    client.url.includes(origin)
                );
                if (focusClient) {
                    focusClient.focus();
                    focusClient.postMessage(inAppNotification);
                } else {
                    if (serviceWorkerScope.clients.openWindow) {
                        serviceWorkerScope.clients
                            .openWindow(serviceWorkerScope.location.origin)
                            .then((windowClient) => {
                                windowClient.postMessage(inAppNotification);
                            });
                    }
                }
            })
    );
};

serviceWorkerScope.addEventListener(
    'push',
    function showPushNotification(event) {
        try {
            const options = {
                icon:
                    'data:image/png;base64,',
            };

            const payload = event.data ? event.data.text() : 'no payload';

            event.waitUntil(
                serviceWorkerScope.registration.showNotification(payload, {
                    tag: Math.random().toString().substr(2),
                    body: `push message`,
                    icon: options.icon,
                })
            );

            // event.waitUntil(
            //     serviceWorkerScope.registration.showNotification(
            //         'How are you now? Please fill the form',
            //         {
            //             tag: Math.random().toString().substr(2),
            //             body: `It will take a few seconds`,
            //             icon: options.icon,
            //             showTrigger:
            //                 serviceWorkerScope.TimestampTrigger &&
            //                 new serviceWorkerScope.TimestampTrigger(
            //                     Date.now() + 3600 * 1000
            //                 ),
            //         }
            //     )
            // );
        } catch (err) {
            console.error(err);
        }
    }
);

serviceWorkerScope.addEventListener('message', function skipWaiting(event) {
    if (event.data === 'skipWaiting') serviceWorkerScope.skipWaiting();
});

serviceWorkerScope.addEventListener('install', function (event) {
    // The promise that skipWaiting() returns can be safely ignored.
    serviceWorkerScope.skipWaiting();
});

serviceWorkerScope.addEventListener('pushsubscriptionchange', function (event) {
    event.waitUntil(
        serviceWorkerScope.registration.pushManager.subscribe({
            userVisibleOnly: true,
        })
    );
});
